import React, { Component } from 'react';
import './content.css'

class Content extends Component {

  constructor(props) {
    super(props)
    this.state = {
      openMessage: null
      }
  }

  openMessage = (id) => {
    this.setState({
      openMessage: id
    })
  }

  closeMessage = () => {
    this.setState({
      openMessage:null
    })
  }


  render() {
    const { email, messages } = this.props.infa
    const { openMessage } = this.state
    return (
      <div className='content'>
        <div id='email'>{email}</div>
        <div id='container'>
          {messages.map(item => {
            return (
              <div className={ openMessage !== item.id ?'message_container' : 'message_contaner open'} key={item.id}>
                <div className='from'>{item.from}</div>
                <div className='message' onClick={() => this.openMessage(item.id)}>{item.message}</div>
                <div className='time'>{item.time}</div>
                <div className='close' onClick={this.closeMessage}>X</div>
              </div>
            )
          })}
        </div>
      </div>
    );
  }
}

export default Content;
